/* Procedure structure for procedure `sp_login` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_login` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_login`(IN id VARCHAR(20), IN pwd VARCHAR(50), IN ip VARCHAR(20), OUT result INT)
BEGIN
	DECLARE _clientid INT DEFAULT 0;
	DECLARE _cnt INT DEFAULT 0;
	
	START TRANSACTION;
	
	SELECT clientID INTO _clientid FROM chan_membership WHERE  userid = id AND userpassword = RIGHT(MD5(pwd),20);
	
	IF _clientid < 1 THEN 
		SET result = 0;	-- 비번 틀림
	ELSE
		SELECT COUNT(*) INTO _cnt FROM clientlist WHERE uid = _clientid AND clientUntilDate >= NOW();
		
		IF _cnt = 1 THEN
			UPDATE chan_membership SET LastLogin = NOW() WHERE userid = id;
			INSERT INTO loginlog (clientID, LoginId, ipAddr, w_date) VALUES (_clientid, id, ip, NOW());
			SET result = _clientid;
		ELSE
			SET result = -1;	-- 사용기간 종료
		END IF;
	END IF;
	
	
    END */$$
DELIMITER ;

